    //Burger menu main
    $(function(){
      $('#nav_toggle').click(function(){
        $("header").toggleClass('open');
        $(".SP-menu").slideToggle(500); //後面數字是淡出淡入的速度
      });
    });

    //Click the item and close the burger menu
    $(function(){
      $('.header .SP-menu').on('click',function(){
        $('#nav_toggle').click();
      });
    });